from django.db.models.manager import Managerclass TaskTodoManager(Manager):    def get_queryset(self):        return super().get_queryset().filter(is_done=False, is_deleted=False)class TaskDoneManager(Manager):    def get_queryset(self):        return super().get_querset().filter(is_done=True, is_deleted=False)class TaskDeleteManager(Manager):    def get_queryset(self):        return super().get_queryset().filter(is_done=False, is_deleted=True)class TaskDoneDeleteManager(Manager):    def get_queryset(self):        return super().get_queryset().filter(is_done=True, is_deleted=True)